	$sexes = array('f','m','x');		$_POST['day'] = (int)$_POST['day'];		$_POST['month'] = (int)$_POST['month'];		$_POST['year'] = (int)$_POST['year'];		$_POST['home'] = cmp_str($_POST['home'],0,75);		if (!in_array($_POST['sex'],$sexes)) {			$_POST['sex'] = 'x';			}		$data = explode('-',date("Y-m-d"));		$rok = $data[0];				if ($_POST['year'] < ($rok - 100)) {			$_POST['year'] = ($rok - 100);			}		if ($_POST['year'] > $rok) {			$_POST['year'] = $rok;			}					if ($_POST['month'] < 1) {			$_POST['month'] = 1;			}		if ($_POST['month'] > 12) {			$_POST['month'] = 12;			}					$days = cal_days_in_month(CAL_GREGORIAN, $_POST['month'], $_POST['year']);				if ($_POST['day'] < 1) {			$_POST['day'] = 1;			}		if ($_POST['day'] > $days) {			$_POST['day'] = $days;			}					if ($_POST['home'] === 'LONG') {			$error = 'Miejsce zamieszkania mo¿e maksymalnie sk³adaæ siê z 75 znaków';			}		elseif ($_POST['home'] === 'SPACES') {			$error = 'Miejsce zamieszkania nie mo¿e sk³adaæ siê z samych spacji';			} else {			$_POST['home'] = parse($_POST['home']);			mysql_query("UPDATE `users` SET `home` = '".$_POST['home']."' WHERE `id` = '".$user['id']."'");			}					mysql_query("UPDATE `users` SET `sex` = '".$_POST['sex']."' , `b_day` = '".$_POST['day']."' , `b_month` = '".$_POST['month']."' , `b_year` = '".$_POST['year']."'WHERE `id` = '".$user['id']."'");				if (is_array($_FILES['image'])) {			if (file_exists($_FILES['image']['tmp_name'])) {				if ($_FILES['image']['size'] < 120000) {					if ($_FILES['image']['type'] == 'image/png' || $_FILES['image']['type'] == 'image/jpeg' || $_FILES['image']['type'] == 'image/gif') {						$size = getimagesize($_FILES['image']['tmp_name']);						if (is_array($size) && $size[0] > 1 && $size[1] > 1) {							if ($size[0] > 240 || $size[1] > 180) {								$error = 'Maksymalne wymiary obrazka to 240x180.';								} else {								if ($_FILES['image']['type'] == 'image/png') {									$format = 'png';									}								if ($_FILES['image']['type'] == 'image/jpeg') {						