d]['do_gracz_name'] = entparse(sql("SELECT `username` FROM `users` WHERE `id` = '".$odz['do_gracza']."'",'array'));	$odznaczenia[$cid]['kolor'] = $odz['kolor'];	$odznaczenia[$cid]['id'] = $odz['id'];	$odznaczenia[$cid]['do_gracz_id'] = $odz['do_gracza'];	$cid++;	}	$tpl->assign('odznaczeni',$odznaczenia);$tpl->assign('error',$error);?>