<?phpif ($_GET['action'] == 'change_page' and isset($_POST['page_RA'])) {	$_POST['page_RA'] = (int)$_POST['page_RA'];	if ($_POST['page_RA'] < 0) {		$_POST['page_RA'] = 0;		}		$_POST['page_RA'] = round($_POST['page_RA']);		$RA_PAGE = $_POST['page_RA'];		$_GET['site'] = floor($_POST['page_RA'] / 20) + 1;		$tpl->assign('page_ra',$RA_PAGE);	}	$rang_decoding = array(	'ally' => 'sqlally',	'player' => 'rang',	'kill_player' => 'killed_units_att_rank',	'kill_playeratt' => 'killed_units_att_rank',	'kill_playerdef' => 'killed_units_def_rank',	'kill_playerall' => 'killed_units_altogether_rank',	);	$tpl->assign('dec_rang',$rang_decoding[$_GET['mode'].$_GET['type']]);$tpl->assign('ally_rang',sql("SELECT `rank` FROM `ally` WHERE `id` = '".$user['ally']."'",'array'));?>