';		}	}	if ($_GET['action'] === 'reset_toolbar') {	if ($_GET['h'] == $session['hkey']) {		mysql_query("UPDATE `users` SET `toolbar` = '".serialize($standard_toolbar)."' WHERE `id` = '".$user['id']."'");		header('location: game.php?village='.$village['id'].'&screen=settings&mode=toolbar');		exit;		}	}	if ($_GET['action'] === 'replace_down') {	if ($_GET['h'] == $session['hkey']) {		$_GET['id'] = (int)$_GET['id'];		$_GET['id'] = floor($_GET['id']);				if (!is_array($toolbar_array[$_GET['id'] + 1])) {			$error = 'Nie mona przesun';			} else {			$last_value = $toolbar_array[$_GET['id'] + 1];			$toolbar_array[$_GET['id'] + 1] = $toolbar_array[$_GET['id']];			$toolbar_array[$_GET['id']] = $last_value;						$toolbar_new_array = serialize($toolbar_array);			mysql_query("UPDATE `users` SET `toolbar` = '".$toolbar_new_array."' WHERE `id` = '".$user['id']."'");				header('location: game.php?village='.$village['id'].'&screen=settings&mode=toolbar');			exit;			}		} else {		$error = 'Bd podczas wykonywania akcji';		}	}	if ($_GET['action'] === 'replace_up') {	if ($_GET['h'] == $session['hkey']) {		$_GET['id'] = (int)$_GET['id'];		$_GET['id'] = floor($_GET['id']);				if (!is_array($toolbar_array[$_GET['id'] - 1])) {			$error = 'Nie mona przesun';			} else {			$next_value = $toolbar_array[$_GET['id'] - 1];			$toolbar_array[$_GET['id'] - 1] = $toolbar_array[$_GET['id']];			$toolbar_array[$_GET['id']] = $next_value;						$toolbar_new_array = serialize($toolbar_array);			mysql_query("UPDATE `users` SET `toolbar` = '".$toolbar_new_array."' WHERE `id` = '".$user['id']."'");				header('location: game.php?village='.$village['id'].'&screen=settings&mode=toolbar');			exit;			}		} else {		$error = 'Bd podczas wykonywania akcji';		}	}	if ($_GET['action'] === 'add_tolbar' && count($_POST) > 0) {	if ($_GET['h'] == $session['hkey']) {		//Walidacja:		$_POST['name'] = cmp_str($_POST['name'],3,25);		$_POST['img_link'] = cmp_str($_POST['img_link'],5,150);		$_POST['link'] = cmp_str($_POST['link'],5,150);				if ($_POST['name'] === 'SHORT') {			$error = 'O nome deve ser enviado em uma escala de pelo menos 3 caracteres.';			}		elseif ($_POST['name'] === 'LONG') {			$error = 'O nome pode ser enviado em uma composio mximo de 25 caracteres.';			}		elseif ($_POST['name'] === 'SPACES') {			$error = 'O nome no pode ser enviado em uma composio do mesmo espao.';			}					if ($_POST['img_link'] === 'SHORT') {			$error = 'O link da imagem que enviou tem de ter uma escala de pelo menos 5 caracteres.';			}		elseif ($_POST['img_link'] === 'LONG') {			$error = 'Link da imagem pode sk enviado em um perodo mximo de 150 caracteres.';			}		elseif ($_POST['img_link'] === 'SPACES') {			$error = 'Link para esta imagem no pode ser enviado em uma composio do mesmo espao.';			}					if ($_POST['link'] === 'SHORT') {			$error = 'Link deve ser enviado em uma escala de pelo menos 5 caracteres.';			}		elseif ($_POST['link'] === 'LONG') {			$error = 'Link moe maksymalnie skada si z 150 znakw.';			}		elseif ($_POST['link'] === 'SPACES') {			$error = 'Link nie moe skada si z samych spacji.';			}					if (count($toolbar_array) > 9) {			$error = 'Pasek narzdzi moe skada si z maksymalnie 10 elementw.';			}					if (empty($error)) {			$toolbar_array[] = array('obrazek' => $_POST['img_link'],'nazwa' => $_POST['name'],'link' => $_POST['link']);						$toolbar_new_array = serialize($toolbar_array);			mysql_query("UPDATE `users` SET `toolbar` = '".$toolbar_new_array."' WHERE `id` = '".$user['id']."'");						header('location: game.php?village='.$village['id'].'&screen=settings&mode=toolbar');			exit;			}		} else {		$error = 'Bd podczas wykonywania akcji';		}	}$tpl->assign('toolbar_array',$toolbar_array);$tpl->assign('error',$error);?>